
# HackBio Stage Zero


from textwrap import wrap

def print_team_member(p):
    
    print("=" * 90)
    print(f"Name: {p['name']}")
    print(f"Slack Username: {p['slack']}")
    print(f"Country: {p['country']}")
    print(f"Hobby: {p['hobby']}")
    print(f"Affiliation: {p['affiliation']}")
    print(f"Favorite Gene: {p['favorite_gene']}")
    print()
    # DNA section 
    print("DNA Sequence:")
    if p.get("fasta_header"):
        print(p["fasta_header"])
    seq = "".join(str(p.get("dna_sequence", "")).split()) 
    if not seq:
        print("(no sequence provided)")
    else:
        for line in wrap(seq, width=60):
            print(line)
    print()  



